import React, { useContext, useEffect } from 'react';

import styles from './Aside.module.scss';
import Separator from '../Separator';
import { LanguageContext } from '../../contexts';
import personalInfos from '../../data/personalInfos';


export default function Aside() {
    const contextLangague = useContext(LanguageContext);
    const language = contextLangague?.language || 'pt-br';
    const json = personalInfos(language);
    console.log(json);

    
    return (
        <aside className={styles.aside}>
            <div className={styles.photo__box}>
                <img src={json.photo} alt="" />
            </div>
            <h2 className={styles.name}>Fabio Abreu (24 anos) {contextLangague?.language}</h2>
            <p className={styles.addres}>Caxias do sul <br /> Rio Grande do Sul - Brasil </p>
            <Separator />
        </aside>
    );
}
