import React, { useContext } from 'react';

import styles from './Aside.module.scss';
import photo from '../../assets/img/my-photo.png';
import Separator from '../Separator';
import Language from '../../contexts';

export default function Aside() {
    const langague = useContext(Language);
    console.log(langague);
    //esperar 2 segundos para mudar o idioma
    setTimeout(() => {
        langague?.setLanguage('en');
    }, 2000);
    console.log(langague);
    
    
    return (
        <aside className={styles.aside}>
            <div className={styles.photo__box}>
                <img src={photo} alt="" />
            </div>
            <h2 className={styles.name}>Fabio Abreu (24 anos)</h2>
            <p className={styles.addres}>Caxias do sul <br /> Rio Grande do Sul - Brasil </p>
            <Separator />
        </aside>
    );
}
